// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165Storage.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract BlendNft is Ownable, ERC721URIStorage {
    using Counters for Counters.Counter;
    using Strings for uint256;
    Counters.Counter public _tokenIds;

    event NFTMinted(address indexed owner, uint256 indexed tokenId);

    mapping(address => uint) _mintCount;

    string _uri =
        "https://gateway.pinata.cloud/ipfs/QmUDnBmE9nUZAVPpA95nvwXczT7hTvmdD2AiwbQumSYjwB"; //4Body7Background1.png

    constructor() ERC721("Avalanche Blend", "FACE") {}

    function mint(string memory ipfsUri) public {
        _uri = ipfsUri;

        _tokenIds.increment();
        uint256 newItemId = _tokenIds.current();
        _mintCount[_msgSender()] += 1;
        _safeMint(_msgSender(), newItemId);
        _setTokenURI(newItemId, _uri);
        emit NFTMinted(_msgSender(), newItemId);
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC721) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

contracts/EmotableRepository.sol.content:
// SPDX-License-Identifier: CC0-1.0

pragma solidity ^0.8.21;

import "./IERC7409.sol";
import "@openzeppelin/contracts/utils/introspection/IERC165.sol";

error BulkParametersOfUnequalLength();
error ExpiredPresignedEmote();
error InvalidSignature();

contract EmoteRepository is IERC7409 {
    bytes32 public immutable DOMAIN_SEPARATOR =
        keccak256(
            abi.encode(
                "ERC-7409: Public Non-Fungible Token Emote Repository",
                "1",
                block.chainid,
                address(this)
            )
        );

    // Used to avoid double emoting and control undoing
    mapping(address emoter => mapping(address collection => mapping(uint256 tokenId => mapping(string emoji => uint256 state))))
        private _emotesUsedByEmoter; // Cheaper than using a bool
    mapping(address collection => mapping(uint256 tokenId => mapping(string emoji => uint256 count)))
        private _emotesPerToken;

    function emoteCountOf(
        address collection,
        uint256 tokenId,
        string memory emoji
    ) public view returns (uint256) {
        return _emotesPerToken[collection][tokenId][emoji];
    }

    function bulkEmoteCountOf(
        address[] memory collections,
        uint256[] memory tokenIds,
        string[] memory emojis
    ) public view returns (uint256[] memory) {
        if (
            collections.length != tokenIds.length ||
            collections.length != emojis.length
        ) {
            revert BulkParametersOfUnequalLength();
        }

        uint256[] memory counts = new uint256[](collections.length);
        for (uint256 i; i < collections.length; ) {
            counts[i] = _emotesPerToken[collections[i]][tokenIds[i]][emojis[i]];
            unchecked {
                ++i;
            }
        }
        return counts;
    }

    function hasEmoterUsedEmote(
        address emoter,
        address collection,
        uint256 tokenId,
        string memory emoji
    ) public view returns (bool) {
        return _emotesUsedByEmoter[emoter][collection][tokenId][emoji] == 1;
    }

    function haveEmotersUsedEmotes(
        address[] memory emoters,
        address[] memory collections,
        uint256[] memory tokenIds,
        string[] memory emojis
    ) public view returns (bool[] memory) {
        if (
            emoters.length != collections.length ||
            emoters.length != tokenIds.length ||
            emoters.length != emojis.length
        ) {
            revert BulkParametersOfUnequalLength();
        }

        bool[] memory states = new bool[](collections.length);
        for (uint256 i; i < collections.length; ) {
            states[i] =
                _emotesUsedByEmoter[emoters[i]][collections[i]][tokenIds[i]][
                    emojis[i]
                ] ==
                1;
            unchecked {
                ++i;
            }
        }
        return states;
    }

    function emote(
        address collection,
        uint256 tokenId,
        string memory emoji,
        bool state
    ) public {
        bool currentVal = _emotesUsedByEmoter[msg.sender][collection][tokenId][
            emoji
        ] == 1;
        if (currentVal != state) {
            if (state) {
                _emotesPerToken[collection][tokenId][emoji] += 1;
            } else {
                _emotesPerToken[collection][tokenId][emoji] -= 1;
            }
            _emotesUsedByEmoter[msg.sender][collection][tokenId][emoji] = state
                ? 1
                : 0;
            emit Emoted(msg.sender, collection, tokenId, emoji, state);
        }
    }

    function bulkEmote(
        address[] memory collections,
        uint256[] memory tokenIds,
        string[] memory emojis,
        bool[] memory states
    ) public {
        if (
            collections.length != tokenIds.length ||
            collections.length != emojis.length ||
            collections.length != states.length
        ) {
            revert BulkParametersOfUnequalLength();
        }

        bool currentVal;
        for (uint256 i; i < collections.length; ) {
            currentVal =
                _emotesUsedByEmoter[msg.sender][collections[i]][tokenIds[i]][
                    emojis[i]
                ] ==
                1;
            if (currentVal != states[i]) {
                if (states[i]) {
                    _emotesPerToken[collections[i]][tokenIds[i]][
                        emojis[i]
                    ] += 1;
                } else {
                    _emotesPerToken[collections[i]][tokenIds[i]][
                        emojis[i]
                    ] -= 1;
                }
                _emotesUsedByEmoter[msg.sender][collections[i]][tokenIds[i]][
                    emojis[i]
                ] = states[i] ? 1 : 0;
                emit Emoted(
                    msg.sender,
                    collections[i],
                    tokenIds[i],
                    emojis[i],
                    states[i]
                );
            }
            unchecked {
                ++i;
            }
        }
    }

    function prepareMessageToPresignEmote(
        address collection,
        uint256 tokenId,
        string memory emoji,
        bool state,
        uint256 deadline
    ) public view returns (bytes32) {
        return
            keccak256(
                abi.encode(
                    DOMAIN_SEPARATOR,
                    collection,
                    tokenId,
                    emoji,
                    state,
                    deadline
                )
            );
    }

    function bulkPrepareMessagesToPresignEmote(
        address[] memory collections,
        uint256[] memory tokenIds,
        string[] memory emojis,
        bool[] memory states,
        uint256[] memory deadlines
    ) public view returns (bytes32[] memory) {
        if (
            collections.length != tokenIds.length ||
            collections.length != emojis.length ||
            collections.length != states.length ||
            collections.length != deadlines.length
        ) {
            revert BulkParametersOfUnequalLength();
        }

        bytes32[] memory messages = new bytes32[](collections.length);
        for (uint256 i; i < collections.length; ) {
            messages[i] = keccak256(
                abi.encode(
                    DOMAIN_SEPARATOR,
                    collections[i],
                    tokenIds[i],
                    emojis[i],
                    states[i],
                    deadlines[i]
                )
            );
            unchecked {
                ++i;
            }
        }

        return messages;
    }

    function presignedEmote(
        address emoter,
        address collection,
        uint256 tokenId,
        string memory emoji,
        bool state,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) public {
        if (block.timestamp > deadline) {
            revert ExpiredPresignedEmote();
        }
        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(
                    abi.encode(
                        DOMAIN_SEPARATOR,
                        collection,
                        tokenId,
                        emoji,
                        state,
                        deadline
                    )
                )
            )
        );
        address signer = ecrecover(digest, v, r, s);
        if (signer != emoter) {
            revert InvalidSignature();
        }

        bool currentVal = _emotesUsedByEmoter[signer][collection][tokenId][
            emoji
        ] == 1;
        if (currentVal != state) {
            if (state) {
                _emotesPerToken[collection][tokenId][emoji] += 1;
            } else {
                _emotesPerToken[collection][tokenId][emoji] -= 1;
            }
            _emotesUsedByEmoter[signer][collection][tokenId][emoji] = state
                ? 1
                : 0;
            emit Emoted(signer, collection, tokenId, emoji, state);
        }
    }

    function bulkPresignedEmote(
        address[] memory emoters,
        address[] memory collections,
        uint256[] memory tokenIds,
        string[] memory emojis,
        bool[] memory states,
        uint256[] memory deadlines,
        uint8[] memory v,
        bytes32[] memory r,
        bytes32[] memory s
    ) public {
        if (
            emoters.length != collections.length ||
            emoters.length != tokenIds.length ||
            emoters.length != emojis.length ||
            emoters.length != states.length ||
            emoters.length != deadlines.length ||
            emoters.length != v.length ||
            emoters.length != r.length ||
            emoters.length != s.length
        ) {
            revert BulkParametersOfUnequalLength();
        }

        bytes32 digest;
        address signer;
        bool currentVal;
        for (uint256 i; i < collections.length; ) {
            if (block.timestamp > deadlines[i]) {
                revert ExpiredPresignedEmote();
            }
            digest = keccak256(
                abi.encodePacked(
                    "\x19Ethereum Signed Message:\n32",
                    keccak256(
                        abi.encode(
                            DOMAIN_SEPARATOR,
                            collections[i],
                            tokenIds[i],
                            emojis[i],
                            states[i],
                            deadlines[i]
                        )
                    )
                )
            );
            signer = ecrecover(digest, v[i], r[i], s[i]);
            if (signer != emoters[i]) {
                revert InvalidSignature();
            }

            currentVal =
                _emotesUsedByEmoter[signer][collections[i]][tokenIds[i]][
                    emojis[i]
                ] ==
                1;
            if (currentVal != states[i]) {
                if (states[i]) {
                    _emotesPerToken[collections[i]][tokenIds[i]][
                        emojis[i]
                    ] += 1;
                } else {
                    _emotesPerToken[collections[i]][tokenIds[i]][
                        emojis[i]
                    ] -= 1;
                }
                _emotesUsedByEmoter[signer][collections[i]][tokenIds[i]][
                    emojis[i]
                ] = states[i] ? 1 : 0;
                emit Emoted(
                    signer,
                    collections[i],
                    tokenIds[i],
                    emojis[i],
                    states[i]
                );
            }
            unchecked {
                ++i;
            }
        }
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual returns (bool) {
        return
            interfaceId == type(IERC7409).interfaceId ||
            interfaceId == type(IERC165).interfaceId;
    }
}

contracts/Equippable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "./RMRKEquippable.sol";
import "./RMRKSoulboundPerToken.sol";
import "./RMRKCatalog.sol";

contract Equippale is
    RMRKEquippable,
    RMRKSoulboundPerToken,
    RMRKCatalog("google.com", "slot")
{
    uint64 tokenId = 1;
    uint64 childId = 1;
    uint64 assetId = 1;
    uint64 groupId = 1;
    uint64 partId = 1;
    uint64[] parentTokens;

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 token_id
    ) internal virtual override(RMRKNestable, RMRKSoulbound) {
        if (!isTransferable(token_id, from, to))
            revert RMRKCannotTransferSoulbound();
    }

    function mint(
        string memory metadataURI,
        address[] memory equippables
    ) public {
        uint64[] memory partIds = new uint64[](3);
        _mint(msg.sender, tokenId, "0x64");
        uint64 length = partId + 3;
        uint index = 0;
        for (uint64 parts = partId; parts < length; parts++) {
            addPart(equippables, metadataURI, parts);
            partIds[index] = parts;
            index++;
            partId++;
        }
        _addAssetEntry(tokenId, groupId, address(this), metadataURI, partIds);
        _addAssetToToken(tokenId, assetId, assetId);
        _acceptAsset(tokenId, 0, assetId);
        parentTokens.push(tokenId);
        tokenId++;
        assetId++;
    }

    function getParentTokens() public view returns (uint64[] memory) {
        return parentTokens;
    }

    function mintChild(uint64 token_id, string memory metadataURI) public {
        uint64[] memory part;
        _mint(msg.sender, tokenId, "0x64");
        _addAssetEntry(tokenId, groupId, address(this), metadataURI, part);
        _addAssetToToken(tokenId, assetId, assetId);
        _acceptAsset(tokenId, 0, assetId);
        addChild(token_id, tokenId);
        acceptChild(token_id, 0, address(this), tokenId);
        tokenId++;
        assetId++;
    }

    function addChild(uint64 token_id, uint64 child_id) internal {
        IERC7401 destContract = IERC7401(address(this));
        destContract.addChild(token_id, child_id, "0x64");
    }

    function addPart(
        address[] memory equippables,
        string memory metadataURI,
        uint64 _partId
    ) public {
        Part memory partData = Part({
            itemType: ItemType.Slot,
            z: 2,
            equippable: equippables,
            metadataURI: metadataURI
        });
        IntakeStruct memory partIntake = IntakeStruct({
            partId: partId,
            part: partData
        });
        _addPart(partIntake);
        _setEquippableToAll(_partId);
    }

    function equipToken(
        uint64 equippableGroupId,
        address parentAddress,
        uint64 slotPartId,
        IntakeEquip memory data
    ) public {
        setValidParentForEquippableGroup(
            equippableGroupId,
            parentAddress,
            slotPartId
        );
        equip(data);
    }

    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        virtual
        override(RMRKEquippable, RMRKSoulbound, RMRKCatalog)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function setValidParentForEquippableGroup(
        uint64 equippableGroupId,
        address parentAddress,
        uint64 slotPartId
    ) public {
        _setValidParentForEquippableGroup(
            equippableGroupId,
            parentAddress,
            slotPartId
        );
    }

    function getTokenId() public view returns (uint64) {
        return tokenId;
    }
}

